        },

        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }


  getModal = value => {
    this.setState({ showModal: value });
  };

  hideModal = value => {
    this.setState({ showModal: 0 });
  };
  componentDidMount() {
    this.setjson();
  }

  render() {
    const { error, isLoaded, items } = this.state;

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="container">
          <h2>Search by name</h2>
          <input
            type="text"
            placeholder="Go search by name: "
            className="search-inp"
            onKeyDown={this.filterSearchForName}
          />
          <table id="content-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Status</th>
                <th>Species</th>
                <th>Type</th>
                <th>Gender</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item, line) => (
                <tr key={line}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.status}</td>
                  <td>{item.species}</td>
                  <td>{item.type}</td>
                  <td>{item.gender}</td>
                  <td>
                    <input
                      type="submit"
                      value="Edit"
                      className="btn btn-edit"
                      onClick={this.props.onHide}
                    ></input>
                  </td>
                  <EditCharacter
                    show={this.state.showModal === item.id}
                    onHide={() => this.hideModal(item.id)}
                    name={item.name}
                  />
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  }
}
