import React, { Component } from "react";

class EditCharacter extends Component {

  sendData(){
    const requestOptions = {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title: 'React PUT Request Example' })
    };
  fetch('http://localhost:3000/'+this.props.id, requestOptions)
      .then(response => response.json())
      .then(data => this.setState({ postId: data.id }));
  }
  render() {
    return (
      <React.Fragment>
        {this.props.show && (
          <div className="modal">

            <form method="PUT" onSubmit={this.sendData} className="change-form">
              <label>ID: {this.props.id}</label>
              <label>Name:</label>
              <input defaultValue={this.props.name}></input>
              <label>Status:</label>
              <input defaultValue={this.props.status}></input>
              <label>Species:</label>
              <input defaultValue={this.props.species}></input>
              <label>Type:</label>
              <input defaultValue={this.props.type}></input>
              <label>Gender:</label>
              <input defaultValue={this.props.gender}></input>
              <input type="submit" value="Change" className="btn"/>
              <button onClick={this.props.onHide} className="btn">Close</button>
            </form>

          </div>
        )}
      </React.Fragment>
    );
  }
}

export default EditCharacter;
