import React, { Component } from "react";

class EditCharacter extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  sendData() {
    const requestOptions = {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
    };
    fetch("http://localhost:9901/", requestOptions)
      .then((response) => response.json())
      //.then((data) => this.setState({ data: data }));
  }
  render() {
    return (
      <React.Fragment>
        {this.props.show && (
          <div className="modal">
            <form
              method="PUT"
              onSubmit={() => {
                this.setState({
                  id: this.props.id,
                  name: this.props.name,
                  status: this.props.status,
                  species: this.props.species,
                  type: this.props.type,
                  gender: this.props.gender,
                });
                this.sendData()
                console.log(this.state);

              }}
              className="change-form">
              <label>ID: {this.props.id}</label>
              <label>Name:</label>
              <input defaultValue={this.props.name}></input>
              <label>Status:</label>
              <input defaultValue={this.props.status}></input>
              <label>Species:</label>
              <input defaultValue={this.props.species}></input>
              <label>Type:</label>
              <input defaultValue={this.props.type}></input>
              <label>Gender:</label>
              <input defaultValue={this.props.gender}></input>
              <input type="submit" value="Change" className="btn" />
              <button onClick={this.props.onHide} className="btn">
                Close
              </button>
            </form>
          </div>
        )}
      </React.Fragment>
    );
  }
}

export default EditCharacter;
