import axios from "axios";
import React, { Component } from "react";

class EditCharacter extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  sendData(obj) {
    var update = {
      id: obj.id,
      name: obj.name,
      status: obj.status,
      species: obj.species,
      type: obj.type,
      gender:obj.gender
    }
    console.log(update)
    axios.put("http://localhost:9901/",update )
      .then(response => {
        console.log("Status: ", response.status);
        console.log("Data: ", response.data);
      }).catch(error => {
        console.error('Something went wrong!', error.response.data);
      });
  }
  handleSubmit = (event) => {
    event.preventDefault();
    console.log(event.target.elements.username.value) // from elements property
    console.log(event.target.username.value)          // or directly
  }
  render() {

    return (
      <React.Fragment>
        {this.props.show && (
          <div className="modal">
            <form
              onSubmit={()=>this.sendData(this.props)}
              className="change-form">
              <label>ID: {this.props.id}</label>
              <label>Name:</label>
              <input defaultValue={this.props.name}></input>
              <label>Status:</label>
              <input defaultValue={this.props.status}></input>
              <label>Species:</label>
              <input defaultValue={this.props.species}></input>
              <label>Type:</label>
              <input defaultValue={this.props.type}></input>
              <label>Gender:</label>
              <input defaultValue={this.props.gender}></input>
              <input type="submit" value="Change" className="btn" />
              <button onClick={this.props.onHide} className="btn">
                Close
              </button>
            </form>
          </div>
        )}
      </React.Fragment>
    );
  }
}

export default EditCharacter;
